bool thresholdGoalPacketLoss(AdaptationOption &gConf) {
    return gConf.verificationResults.packetLoss < 10;
}

bool optimizationGoalPacketLoss(AdaptationOption &gConf, AdaptationOption &tConf) {
    return tConf.verificationResults.packetLoss < gConf.verificationResults.packetLoss;
}

bool optimizationGoalEnergyCosnumption(AdaptationOption &gConf, AdaptationOption &tConf) {
    return gConf.verificationResults.energyConsumption > tConf.verificationResults.energyConsumption;
}

bool equalConfigurations(ManagedSystem &c1, ManagedSystem &c2){
    int i;
    Mote m1, m2;
    for (m:mote_ids){
        m1 = c1.motes[m];
        m2 = c2.motes[m];
        if (m1.parents != m2.parents)
            return false;
        for(i = 0; i < m1.parents; i++){
            if (m1.links[i] != m2.links[i])
                return false;
        }
    }
    return true;
}

