typedef struct{
    int source;
    int destination;
    int multiplier;
    int constant;
}SNREquation;

SNREquation snrEquations[MAX_LINKS]={
{2,	4,	2,	740},
{3,	1,	50,	124},
{4,	1,	83,	-812},
{5,	9,	49,	-449},
{6,	4,	62,	-981},
{7,	3,	59,	-664},
{7,	2,	54,	-205},
{8,	1,	53,	-10},
{9,	1,	83,	-729},
{10, 6, 82, -733},
{10,	5,	65,	-300},
{11,	7,	71,	-320},
{12,	7,	93,	-1620},
{12,	3,	10,	600},
{13,	11,	61,	-360},
{14,	12,	49,	-477},
{15,	12,	59,	-719}};

int getSNR(int source, int destination, int power){
    int i, SNR;
    for(i= 0 ; i < MAX_LINKS; i++){
        if (snrEquations[i].source == source && snrEquations[i].destination == destination){
            SNR = (((snrEquations[i].multiplier) * power + (snrEquations[i].constant))/100);
            return SNR;
        }
    }
    return 0;
}

Link getLink(Mote mote, int destination){
    return mote.links[0].destination == destination?mote.links[0]: mote.links[1];
}

