// If a service configuration is changed then add it into the plan
void addStep(int stepType, Link link, int value){
    Step step = {stepType, link, value};
    K.plan.steps[K.plan.totalSteps++] = step;
}

bool hasLink(){
     return linkCount < MAX_LINKS;
}
Mote mote;
Mote prevMote;
Link prevLink;
bool firstLink = true;

Link nextLink(){
    Link newLink;

        mote = bestAdaptationOption.deltaIoT.motes[moteCount];
        prevMote = K.currentConfig.deltaIoT.motes[moteCount];

    if (mote.parents == 1){
        moteCount++;
        newLink = mote.links[0];
        prevLink = prevMote.links[0];
    }
    else if (mote.parents == 2){
        if (firstLink){
            newLink = mote.links[0];
            prevLink = prevMote.links[0];
            firstLink = false;
        }
        else{
            newLink = mote.links[1];
            prevLink = prevMote.links[1];
            firstLink = true;
            moteCount++;
        }
    }

    linkCount++;
    return newLink;
}

bool hasStep(){
    return stepType != NONE && stepCount < MAX_STEPS;
}

int nextStepType(){
    if (stepCount == 0 && link.distribution != prevLink.distribution){
        stepCount = CHANGE_DIST;
    }
    else if(link.powerSetting != prevLink.powerSetting){
        stepCount = CHANGE_POWER;
    }
    else{
        stepCount = NONE;
    }
    return stepCount;
}

void resetSteps(){
    stepCount = 0;
}

void selectBestAdaptationOption(){
    int i, bestIndex=-1;
    bool packetLossSatisfied, energyConsumptionSatisfied;

    //bestAdaptationOption = EMPTY; //K.adaptationOptions.options[0];

    for(i = 0; i < K.adaptationOptions.size; i++){
        packetLossSatisfied = thresholdGoalPacketLoss(K.adaptationOptions.options[i]);
        if (bestIndex != -1)
            energyConsumptionSatisfied = optimizationGoalEnergyCosnumption(K.adaptationOptions.options[bestIndex], K.adaptationOptions.options[i]);
        else
            energyConsumptionSatisfied = true;
        if (packetLossSatisfied && energyConsumptionSatisfied){
            //bestAdaptationOption = K.adaptationOptions.options[i];
            bestIndex = i;
        }
    }
    if (bestIndex != -1)
        bestAdaptationOption = K.adaptationOptions.options[bestIndex];
    else
        bestAdaptationOption = EMPTY;
}

bool validAdaptationOption(){
    return bestAdaptationOption != EMPTY;
}

bool bestAdaptationOptionInUse(){
    return K.currentConfig.deltaIoT == bestAdaptationOption.deltaIoT;
}

void useFailSafeStrategy(){
    int i, bestIndex=0;
    bool energyConsumptionSatisfied;

    for(i = 1; i < K.adaptationOptions.size; i++){
            energyConsumptionSatisfied = optimizationGoalEnergyCosnumption(K.adaptationOptions.options[bestIndex], K.adaptationOptions.options[i]);
        if (energyConsumptionSatisfied){
            bestIndex = i;
        }
    }

    bestAdaptationOption = K.adaptationOptions.options[bestIndex];

/*
